INSERT INTO `joke` VALUES ('349', '0', '2016-05-02 16:41:39.000000', '2016-05-02 16:41:39.000000', '瞅你咋的', '公司新来的单身美女总裁对我有好感，这事我也跟我妈说了。我妈叫我好好把握机会，总裁是黑龙江人，个子高，腿又长，长得好看。', '', '2');
