INSERT INTO `joke` VALUES ('354', '0', '2016-05-02 16:41:40.000000', '2016-05-02 16:41:40.000000', '最痛苦的事', '人生最痛苦的事莫过于好不容易挨完了好长的广告，然后发现点错集数了～', '', '2');
