INSERT INTO `joke` VALUES ('800', '0', '2016-05-02 16:42:48.000000', '2016-05-02 16:42:48.000000', '感受下', '我有个女闺蜜，每逢她老公过生日，她就会送他最新的电子产品，从最早的iPad顶配到各种单反镜头，再到高配电脑给他玩游戏。', '', '2');
