INSERT INTO `joke` VALUES ('905', '0', '2016-05-02 16:43:08.000000', '2016-05-02 16:43:08.000000', '我家女儿今天放学后告诉我', '我家女儿今天放学后告诉我：“妈妈，老师夸我漂亮了。”我问：“咋夸得？”“老师说你这个小姑娘长这么漂亮，字咋写的这么难看呢？”', '', '2');
