INSERT INTO `joke` VALUES ('927', '0', '2016-05-02 16:43:12.000000', '2016-05-02 16:43:12.000000', '怎么读', '我给女友发短信：“如果我长的吓人你还喜欢我吗？”', '', '2');
INSERT INTO `joke` VALUES ('928', '0', '2016-05-02 16:43:12.000000', '2016-05-02 16:43:12.000000', '好奇', '去医院接老婆回家，看见一大约四五岁的孩子走到井盖边，趴下说着什么，出于好奇，悄悄跟过去，只听这熊孩子说：忍者神龟在不在家？', '', '2');
INSERT INTO `joke` VALUES ('929', '0', '2016-05-02 16:43:12.000000', '2016-05-02 16:43:12.000000', '鸭梨山大', '现在的学生真挺不容易的，鸭梨山大啊，隔壁班的那个叫盈盈的童鞋肯定又被老师留下来补课了。', '', '2');
