INSERT INTO `joke` VALUES ('942', '0', '2016-05-02 16:43:14.000000', '2016-05-02 16:43:14.000000', '蹲厕所', '朋友媳妇蹲厕所20分钟了，我们都在等她，我说：怎么这么慢，怎么回事？', '', '2');
