INSERT INTO `joke` VALUES ('944', '0', '2016-05-02 16:43:14.000000', '2016-05-02 16:43:14.000000', '当猪', '嫦娥将天蓬调戏他的事告到了玉帝处。玉帝问太白金星：天蓬此举该如何处置？', '', '2');
